import React, { useEffect, useState } from "react";


const Chatbot = () => {
  const [loading, setLoading] = useState(true);

  return (
    <div className="chatpage">
        <div class="center-div">
          {loading && <div className="loading">loading</div>} 
          <iframe
          title="Chat"
          srcDoc={`
            <body>
              <script src="https://cdn.botpress.cloud/webchat/v1/inject.js"></script>
              <script>
                window.botpressWebChat.init({
                  composerPlaceholder: 'Chat with PetPals',
                  botConversationDescription: 'PetPals your new go-to for pet care concerns. From sudden symptoms to routine queries, get expert advice anytime, anywhere.',
                  botId: 'dfb709ab-173b-4fc1-a012-17ec7d590405',
                  hostUrl: 'https://cdn.botpress.cloud/webchat/v1',
                  messagingUrl: 'https://messaging.botpress.cloud',
                  clientId: 'dfb709ab-173b-4fc1-a012-17ec7d590405',
                  webhookId: '3e07ef32-ae2f-4774-b0b2-f0bbb0fbed96',
                  lazySocket: true,
                  themeName: 'prism',
                  botName: 'PetPals',
                  stylesheet: 'https://webchat-styler-css.botpress.app/prod/code/3fcd3e4e-d5bc-4bf5-8699-14b621b3ada2/v31782/style.css',
                  frontendVersion: 'v1',
                  showBotInfoPage: true,
                  enableConversationDeletion: true,
                  theme: 'prism',
                  themeColor: '#2563eb'
                });
              </script>
            </body>
          `}
          width="100%"
          height="100%"
        ></iframe>
        </div>
    </div>
  );
};

export default Chatbot;
